import React, { Component } from "react";
import { Col, Row, Container, Card } from "react-bootstrap";
import axios from 'axios';

export default class CompanyShowcase extends Component {
    constructor(props) {
        super(props);
        this.state = {
            companies: []
        };
    }

    componentDidMount() {
        axios.get("http://localhost:8080/companies")
            .then(response => {
                console.log(response.data);
                this.setState({ companies: response.data });
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }

    render() {
        return (
            <div>
                <Container>
                    <h2 className="text-center mt-5">Company Showcase</h2>
                    <Row xs={1} md={2} lg={3} className="g-4 mt-3">
                        {this.state.companies.map(company => (
                            <Col key={company._id}>
                                <Card className="h-100">
                                    <Card.Img variant="top" src={`http://localhost:8080/${company.imagePath}`} />
                                    <Card.Body>
                                        <Card.Title>{company.name}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </Container>
            </div>
        );
    }
}
