import React, { Component } from 'react';
import axios from 'axios';
import { Card, CardContent, CardMedia, Typography, Grid } from '@mui/material';

export default class CompanyShowcase extends Component {
    constructor(props) {
        super(props);
        this.state = {
            companies: [],
        };
    }

    componentDidMount() {
        // Fetch company images from the backend
        axios.get('http://localhost:8080/company/images')
            .then(response => {
                this.setState({ companies: response.data });
            })
            .catch(error => {
                console.error('Error fetching companies:', error);
            });
    }

    render() {
        return (
            <div>
                <h2>Company Showcase</h2>
                <Grid container spacing={3}>
                    {this.state.companies.map((company, index) => (
                        <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
                            <Card>
                                <CardMedia
                                    component="img"
                                    height="200"
                                    image={`http://localhost:8080/backendImages/${company.imagePath}`}
                                    alt={`Company ${index + 1}`}
                                />
                                <CardContent>
                                    <Typography variant="h6" component="div">
                                        {`Company ${index + 1}`}
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </div>
        );
    }
}
