import React, { Component } from 'react';
import axios from 'axios';

export default class CompanyShowcase extends Component {
    constructor(props) {
        super(props);
        this.state = {
            companies: [],
        };
    }

    componentDidMount() {
        axios.get('http://localhost:8080/company/images')
            .then(response => {
                this.setState({ companies: response.data });
            })
            .catch(error => {
                console.error('Error fetching companies:', error);
            });
    }

    render() {
        return (
            <div>
                <h2>Company Showcase</h2>
                <div className="gallery">
                    {this.state.companies.map(company => (
                        <div key={company._id} className="gallery-item">
                            <img src={`http://localhost:8080/${company.imagePath}`} alt={company.name} />
                            <div className="company-name">{company.name}</div>
                        </div>
                    ))}
                </div>
            </div>
        );
    }
}
