import React, { Component } from 'react';
import axios from 'axios';
import { Card, CardMedia, Grid } from '@mui/material';

export default class CompanyShowcase extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imagePaths: [],
        };
    }

    componentDidMount() {
        // Fetch company images from the backend server
        axios.get('http://localhost:8080/company/images')
            .then(response => {
                this.setState({ imagePaths: response.data });
            })
            .catch(error => {
                console.error('Error fetching company images:', error);
            });
    }

    render() {
        const { imagePaths } = this.state;

        return (
            <div>
                <h2>Company Showcase</h2>
                <Grid container spacing={2}>
                    {imagePaths.map((imagePath, index) => (
                        <Grid item xs={12} sm={6} md={4} key={index}>
                            <Card>
                                <CardMedia
                                    component="img"
                                    height="200"
                                    image={`http://localhost:8080/${imagePath}`}
                                    alt={`Company ${index + 1}`}
                                />
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </div>
        );
    }
}
